package anushkaAttempt;

public class anEvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 0;
		
				 
		while (i <= 20) {
		 
			if (i%2 == 0) { 
				System.out.println(" " + i + " is an even number");
			} else {
				System.out.print( " ");
	}
			i = i + 1; 
	}
		System.out.println(" ");
	
		  int j = 0;
		  
	while (j <= 20) {
	 
		if (j%2 != 0) { 
			System.out.println(" " + j + " is an odd number");
		} else {
			System.out.print( " ");
}
		j = j + 1; 
}
}
}