package anushkaAttempt;

import java.util.Scanner;

public class whileTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// NEED TO PROOF CHECK!!!
		Scanner reader = new Scanner(System.in);
		
		int f;
		int l;
		int b = 0;
		int a;
		
		do {
		System.out.println("Enter your factor:");	
		f = reader.nextInt();
		
		System.out.println("Enter the desired length:");
		l = reader.nextInt();
		
		} while (l >= b);{
			
			a = f * b; 
			
			System.out.println( f + "*" + b  + "=" + a );
			
			
			
		
			
		}
	}
}

